<!DOCTYPE html>
<html lang="en">
	<head>
		<meta charset="UTF-8">
		<title>SKPWEBSITE - Contact</title>
		<meta http-equiv="X-UA-Compatible" content="IE=edge">
		<meta name="viewport" content="width=device-width, initial-scale=1">
		<link rel="icon" type="image/png" href="../favicon.png">
		<link rel="canonical" href="http://www.skpwebsite.com">
		<meta name="description" content="">
		<link rel="stylesheet" href="{{ URL::asset('css/app.css') }}">
	</head>
	<body>
<div class="login-m-m">
  <div class="logo-m-m"><a href="../"><img src="data:image/svg+xml;base64," alt="skpwebsite"/></a><a href="../">
      <div class="cross-log"><span> </span><span> </span></div></a></div>
  <div class="m-m1 hide">
    <h1>Create A/C | <span class="login-page-mm create-b">LOG IN</span></h1>
    <center><p class="mssg animated flash" style="color: green; display: none;"></p>
        <div class="mssgerrer" style="color: red; display: none;"></div></center>
    <form action="#" id="createnew">
      <input type="text" name="fullname-mm" id="fullnamea" required="" class="fullname inco"/>
      <label for="fullname-mm" class="fullname-l laco"><span>Full Name</span></label>

      <input type="email" name="email-mm" id="emaila" required="" class="email inco"/>
      <label for="email-mm" class="email-l laco"><span>Email</span></label>

      <input type="password" name="email-mm" id="passworda" required="" class="email inco"/>
      <label for="email-mm" class="email-l laco"><span>New Password</span></label><br/><br/>
      <input type="submit" name="submit" value="SEND" class="submit -mm s1-item2"/>
      <input type="hidden" name="_token" value="{{ csrf_token() }}">
    </form>
  </div>
  <div class="m-m2">
    <h1>Log In | <span class="login-page-mm log-in-b">Create</span></h1>
    <center><p class="mssg1 animated flash" style="color: green; display: none;"></p>
            <div class="mssgerrer1" style="color: red; display: none;"></div></center>
    <form action="#" id="loginnew">
      <input type="email" name="fullname-mm" id="usernameb" required="" class="fullname inco"/>
      <label for="fullname" class="fullname-l laco"><span>Username(email)</span></label>
      <input type="password" name="email-mm" id="passwordb" required="" class="email inco"/>
      <label for="email-mm" class="email-l laco"><span>Password</span></label><br/><br/>
      <input type="submit" name="submit" value="SEND" class="submit -mm s1-item2"/>
      <input type="hidden" name="_token" value="{{ csrf_token() }}">
    </form>
  </div>
</div>

<div class="loading" style="display: none"><div></div></div>

<script src="{{ URL::asset('js/app.js') }}"></script>

<script>
	$(document).ready(function(){
        $('.create-b , .log-in-b').click(function(){
          $('.m-m1, .m-m2').toggleClass('hide')
        })
      });

  $('#createnew').submit(function(e){
    $('.loading').show();
    e.preventDefault();
    var fullname= $('#fullnamea').val(),
        email = $('#emaila').val(),
        password =$('#passworda').val();
    $.ajax({
      type: 'post',
      url: 'creatnew',
      data: {fullname: fullname, email: email,password: password},
      success: function(data){
                    $('.mssgerrer').hide();
                    $('.mssg').show();

                    console.log(data);
                    if($.isArray(data)){
                      $('.mssgerrer').show();
                      var datal= "";
                        for (var k = 0; k < data.length; k++) {
                          datal += '<p>' +data[k] +'</p>';
                        }
                      $('.mssgerrer').html(datal);
                    }else{
                      window.location.replace(data);
                    }

                    $('.loading').hide();
                    // $('#fullnamea').val('');
                    // $('#emaila').val('');
                    // $('#passworda').val('');
      },
      error: function(jqXHR, exception){
                    $('.mssg').hide();
                    $('.mssgerrer').show();
                        var msg = '';
                        if (jqXHR.status === 0) {
                            msg = 'Not connect.\n Verify Network.';
                        } else if (jqXHR.status == 404) {
                            msg = 'Requested page not found. [404]';
                        } else if (jqXHR.status == 500) {
                            msg = 'Internal Server Error [500].';
                        } else if (exception === 'parsererror') {
                            msg = 'Requested JSON parse failed.';
                        } else if (exception === 'timeout') {
                            msg = 'Time out error.';
                        } else if (exception === 'abort') {
                            msg = 'Ajax request aborted.';
                        } else {
                            msg = 'Uncaught Error.';
                        }
                        $('.mssgerrer').html(msg);
                        $('.loading').hide();
      }
    })

  })


  $('#loginnew').submit(function(e){
    $('.loading').show();
    e.preventDefault();

    var username = $('#usernameb').val(),
        password = $('#passwordb').val();
    $.ajax({
      type: 'post',
      url: 'login',
      data: {username: username,password: password},
      success: function(data){
                    $('.mssgerrer1').hide();
                    $('.mssg1').show();
                    console.log(data);
                    if(data == 'Fail'){
                      $('.mssgerrer1').show();
                      $('.mssgerrer1').html('Authentication Fail please try agen.');
                    }else{
                      window.location.replace(data);
                    }

                    $('.loading').hide();
      },
      error: function(jqXHR, exception){
                    $('.mssg1').hide();
                    $('.mssgerrer1').show();
                        var msg = 'Errer';
                        if (jqXHR.status === 0) {
                            msg = 'Not connect.\n Verify Network.';
                        } else if (jqXHR.status == 404) {
                            msg = 'Requested page not found. [404]';
                        } else if (jqXHR.status == 500) {
                            msg = 'Internal Server Error [500].';
                        } else if (exception === 'parsererror') {
                            msg = 'Requested JSON parse failed.';
                        } else if (exception === 'timeout') {
                            msg = 'Time out error.';
                        } else if (exception === 'abort') {
                            msg = 'Ajax request aborted.';
                        } else {
                            msg = 'Uncaught Error';
                        }
                        $('.mssgerrer1').html(msg);
                        $('.loading').hide();
      }
    })

  })
</script>
